function binarySearch($arr,$k){
    $len = count($arr);
    $low = 0;
    $high = $len-1;
    while($low <= $high) {
        $mid = intval(($low + $high) / 2);
        if ($arr[$mid] == $k) {
            return $mid;
        } elseif ($arr[$mid] > $k) {
            $high = $mid-1;
        } else {
            $low = $mid+1;
        }
    }
    return false;

}

