function binarySearch ($array, $val ){
    $low=0;
    $high=count($array)-1;
    while( $low<=$high ){
        if($low==$high){
            $mid=$low;
        }else{
            $mid= $low +( ( $val -$array[$low] )/( $array[$high]-$array[$low] ) ) * (  $high - $low  ) ;
            if($mid<0)return false;
        }
        if( $val<$array[$mid] ){
            $high=$high-1;
        }else if( $val>$array[$mid] ){
            $low=$low+1;
        }else{
            return $mid;
        }
    }
    return false;
}

